#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define INPUT ""

/* use fgetc() instead if input is stored in a file */
char next_char(int reset)
{
    static int index;
    
    if (reset)
        index = 0;
        
    return INPUT[index++];
}

void add_char(char c, int *count, int *duplicate) 
{
    count[c] += 1;
    if (count[c] > 1) {
        *duplicate += 1;
    }
}

void del_char(char c, int *count, int *duplicate) 
{
    count[c] -= 1;
    if (count[c] >= 1) {
        *duplicate -= 1;
    }
}

int get_sequence_index(int word_size) 
{
    int count[256] = {0,};
    int k, duplicate, index;
    char *window;
    
    window = malloc(word_size);
    if (window == NULL)
        return -1;

    duplicate = 0;
    /* fill the first word */
    for (index = 0 ; index < word_size ; index ++) {
        char c = next_char((index==0)?1:0);
        window[index] = c;
        add_char(c, count, &duplicate);
    }
    
    /* slide the word until we find unique characters */
    while (duplicate > 0) {
        char c = next_char(0);
        int k;
        
        /* special value to raise eof */
        if (c == 0xff)
            break;

        k = index % word_size;
        del_char(window[k], count, &duplicate);
        
        window[k] = c;
        add_char(c, count, &duplicate);
        
        index += 1;
    }
    
    free(window);

    return (duplicate == 0) ? index : -1;
}

int main()
{
    printf("Part1: %d\n", get_sequence_index(4));
    printf("Part2: %d\n", get_sequence_index(14));

    return 0;
}
